require.config({
    baseUrl: window.location.protocol + "//" + window.location.host + "/" + window.location.pathname.split('/')[1],
    paths: {
        'jquery': 'js/jquery/jquery-1.9.1-min',
        'jquery-ui': 'js/jquery-ui/jquery-ui-1.10.3.custom-min',
        'prime-ui': 'js/primeui/primeui-0.9.6-min',
        'rgbcolor': 'js/canvag/rgbcolor-min',
        'StackBlur': 'js/canvag/StackBlur-min',
        'canvg': 'js/canvag/canvg-min',
        'eve': 'js/raphael/eve-min',
        'raphael-core': 'js/raphael/raphael.core-min',
        'raphael-svg': 'js/raphael/raphael.svg-min',
        'raphael-vml': 'js/raphael/raphael.vml-min',
        'raphael': 'js/raphael/raphael.amd-min',
        'raphael-zpd': 'js/raphael/raphael.zpd-min',
        'raphael-svg-export': 'js/raphael/raphael.svg.export-min',
        /*taitale tools*/
        'taitale-cylinder': 'js/taitale/tools/cylinder-min',
        'taitale-dictionaries': 'js/taitale/tools/dictionaries-min',
        'taitale-ext-string': 'js/taitale/tools/ext.string-min',
        'taitale-ext-raphael': 'js/taitale/tools/ext.raphael-min',
        'taitale-helper': 'js/taitale/tools/helper-min',
        'taitale-matrix': 'js/taitale/tools/matrix-min',
        'taitale-loader': 'js/taitale/tools/loader-min',
        'taitale-params': 'js/taitale/tools/params-min',
        'taitale-prototypes': 'js/taitale/tools/prototypes-min',
        /*taitale core*/
        'taitale-map-options': 'js/taitale/core/map/options-min',
        'taitale-map-matrix': 'js/taitale/core/map/matrix-min',
        'taitale-map': 'js/taitale/core/map/map-min',
        'taitale-container-matrix': 'js/taitale/core/container/matrix-min',
        'taitale-container-hat': 'js/taitale/core/container/hat-min',
        'taitale-container': 'js/taitale/core/container/container-min',
        'taitale-node-matrix': 'js/taitale/core/node/matrix-min',
        'taitale-node': 'js/taitale/core/node/node-min',
        'taitale-endpoint': 'js/taitale/core/endpoint-min',
        'taitale-transport': 'js/taitale/core/transport/transport-min',
        'taitale-transport-multicastbus': 'js/taitale/core/transport/multicastBus-min',
        'taitale-link': 'js/taitale/core/link-min',
        /*taitale bubble tree layout*/
        'taitale-btree': 'js/taitale/layout/bubbletree/btree-min',
        'taitale-bvertex': 'js/taitale/layout/bubbletree/bvertex-min',
        /*taitale middleware layout*/
        'taitale-map-splitter': 'js/taitale/layout/middleware/mapSplitter-min',
        'taitale-layoutntw-registries' : 'js/taitale/layout/middleware/registries-min',
        'taitale-datacenter': 'js/taitale/layout/middleware/datacenter/datacenter-min',
        'taitale-datacenter-splitter': 'js/taitale/layout/middleware/datacenter/dcSplitter-min',
        'taitale-datacenter-hat': 'js/taitale/layout/middleware/datacenter/hat-min',
        'taitale-datacenter-matrix': 'js/taitale/layout/middleware/datacenter/matrix-min',
        'taitale-area': 'js/taitale/layout/middleware/area/area-min',
        'taitale-area-hat': 'js/taitale/layout/middleware/area/hat-min',
        'taitale-area-matrix': 'js/taitale/layout/middleware/area/matrix-min',
        'taitale-lan': 'js/taitale/layout/middleware/lan/lan-min',
        'taitale-lan-matrix': 'js/taitale/layout/middleware/lan/matrix-min',
        'taitale-lan-hat': 'js/taitale/layout/middleware/lan/hat-min'
    },
    shim: {
        "jquery-ui": {
            exports: "$",
            deps: ['jquery']
        },
        "prime-ui": {
            exports: "$",
            deps: ['jquery','jquery-ui']
        },
        "canvg": {
            exports: "canvg",
            deps: ['rgbcolor', 'StackBlur']
        }
    }
});

requirejs (
    [
        'prime-ui',
        'canvg',
        'taitale-helper',
        'taitale-loader',
        'taitale-dictionaries',
        'taitale-map-options'
    ],
    function ($,canvg,helper,loader,dictionaries,mapOptions) {

        var loader_   = new loader(),
            dic       = new dictionaries(),
            options   = new mapOptions(),
            helper_   = new helper(),
            homeURI   = window.location.protocol + "//" + window.location.host + "/" + window.location.pathname.split('/')[1];

        helper_.initGrowlMsgs('#mapGrowl');

        var test = $('#test'),
            layout = $('#layout'),
            edition = $('#edition'),
            epreset = $('#epreset'),
            notifyI = $('#notifyInfo'),
            notifyW = $('#notifyWarn'),
            notifyE = $('#notifyErrs'),
            reload = $('#reload'),
            nsize = $('#nsize'),
            center = $('#center'),
            details = $('#details'),
            JPG = $('#JPG'),
            PNG = $('#PNG'),
            SVG = $('#SVG'),
            mapExport = $('#mapExport'),
            imgExport = $('#imgExport'),
            displayDC = $("#displayDC"),
            displayArea = $("#displayArea"),
            displayLan = $("#displayLan"),
            rootTreeSorting = $('#rootTreeSorting'),
            subTreesSorting = $('#subTreesSorting');

        test.puidropdown({
            change: function() {
                options.setURI(homeURI + "/js/taitale.samples/json/sample.taitale.input."+test.val()+".json");
                try {
                    loader_.reloadMap(options);
                    loader_.editionMode(options);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to load map',
                            detail: 'Name: '+test.val()+'<br>Layout: '+options.getLayout(),
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        layout.puidropdown({
            change: function() {
                options.setLayout(layout.val());
                try {
                    if (options.getLayout()===dic.mapLayout.MDW) {
                        document.getElementById('middlewareOptions').style.display = "";
                        document.getElementById('treeOptions').style.display    = "none";
                    } else if (options.getLayout()===dic.mapLayout.BBTREE) {
                        document.getElementById('middlewareOptions').style.display = "none";
                        document.getElementById('treeOptions').style.display    = "";
                    } else {
                        document.getElementById('middlewareOptions').style.display = "none";
                        document.getElementById('treeOptions').style.display    = "none";
                    }
                    loader_.rebuildMap(options);
                    loader_.editionMode(options);
                    loader_.displayDC(displayDC[0].checked);
                    loader_.displayArea(displayArea[0].checked);
                    loader_.displayLan(displayLan[0].checked);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to load map',
                            detail: 'Name: '+test.val()+'<br>Layout: '+options.getLayout(),
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        edition.puicheckbox({
            change: function() {
                options.edition = edition[0].checked;
                try {
                    loader_.editionMode(options);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to refresh map',
                            detail: 'Name: ' + test.val() + '<br>Layout: ' + options.getLayout(),
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                    var msg = "<h3>oO ! We have some problem here ! <br/> Let's find a way to correct it ... </h3>" +
                        '<p>1) open a new JIRA ticket <a href="http://jira.echinopsii.net" target="_blank">here</a></p>' +
                        '<p>2) complete the ticket : <ul>' +
                        '<li>attach <a href="'+ options.getURI() +'" target="_blank">the source of the problem</a></li>'+
                        '<li>specify the layout (' + options.getLayout() +')</li></ul></p>' +
                        "<p>3) wait the ticket to be resolved ... </p>";
                    helper_.showErrorBox(msg);
                }
            }
        });
        epreset.puicheckbox({
            change: function() {
                options.epreset = epreset[0].checked;
                try {
                    loader_.endpointReset(options);
                }catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to refresh map',
                            detail: 'Name: ' + test.val() + '<br>Layout: ' + options.getLayout(),
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                    var msg = "<h3>oO ! We have some problem here ! <br/> Let's find a way to correct it ... </h3>" +
                        '<p>1) open a new JIRA ticket <a href="http://jira.echinopsii.net" target="_blank">here</a></p>' +
                        '<p>2) complete the ticket : <ul>' +
                        '<li>attach <a href="'+ options.getURI() +'" target="_blank">the source of the problem</a></li>'+
                        '<li>specify the layout (' + options.getLayout() +')</li></ul></p>' +
                        "<p>3) wait the ticket to be resolved ... </p>";
                    helper_.showErrorBox(msg);
                }
            }
        });
        notifyI.puicheckbox({
            change: function() {
                helper_.setNotifyInfo(notifyI.puicheckbox('isChecked'));
            }
        });

        notifyW.puicheckbox({
            change: function() {
                helper_.setNotifyWarn(notifyW.puicheckbox('isChecked'));
            }
        });
        notifyE.puicheckbox({
            change: function() {
                helper_.setNotifyErrs(notifyE.puicheckbox('isChecked'));
            }
        });
        nsize.puibutton({
            click: function() {
                try {
                    loader_.normalSize();
                    nsize.removeClass('ui-state-focus');
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to center map',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        center.puibutton({
            click: function() {
                try {
                    loader_.centerMappy();
                    center.removeClass('ui-state-focus');
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to center map',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        reload.puibutton({
            click: function() {
                try {
                    loader_.reloadMap(options);
                    loader_.editionMode(options);
                    reload.removeClass('ui-state-focus');
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to reload map',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });

        mapExport.puidialog({
            showEffect: 'fade',
            hideEffect: 'fade',
            width: 1000,
            height:800
        });

        JPG.puibutton({
            click: function() {
                try {
                    var svg          = loader_.exportToSVG(),
                        exportCanvas = '<canvas id="exportCanvas" title="jpgMap"></canvas>';
                    imgExport.empty();
                    imgExport.append(exportCanvas);
                    canvg('exportCanvas', svg);
                    document.getElementById("exportCanvas").toDataURL("image/jpeg", 1.0);
                    mapExport.puidialog('show');
                    JPG.removeClass('ui-state-focus');
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to export map to PNG',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        PNG.puibutton({
            click: function() {
                try {
                    var svg    = loader_.exportToSVG(),
                        imgsrc = "data:image/svg+xml," + encodeURIComponent(svg),
                        img    = '<canvas id="exportPngCanvas" title="pngMap"></canvas>';
                    imgExport.empty();
                    imgExport.append(img);
                    canvg('exportPngCanvas', svg);
                    document.getElementById("exportPngCanvas").toDataURL("image/png");
                    mapExport.puidialog('show');
                    PNG.removeClass('ui-state-focus');
                    //open(imgsrc);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to export map to PNG',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        SVG.puibutton({
            click: function() {
                try {
                    var svg    = loader_.exportToSVG(),
                        imgsrc = "data:image/svg+xml," + encodeURIComponent(svg),
                        img    = '<img src="'+imgsrc+'" title="svgMap">';
                    imgExport.empty();
                    imgExport.append(img);
                    mapExport.puidialog('show');
                    SVG.removeClass('ui-state-focus');
                    //open(imgsrc);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to export map to SVG',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });

        displayDC.puicheckbox({
            change: function() {
                try {
                    options.displayDC = displayDC[0].checked;
                    loader_.displayDC(options.displayDC);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to display/hide DCs',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        displayArea.puicheckbox({
            change:function() {
                try {
                    options.displayAREA = displayArea[0].checked;
                    loader_.displayArea(options.displayAREA);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to display/hide Areas',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        displayLan.puicheckbox({
            change: function() {
                try {
                    options.displayLAN = displayLan[0].checked;
                    loader_.displayLan(options.displayLAN);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed to display/hide Lans',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });

        rootTreeSorting.puidropdown({
            change: function() {
                try {
                    options.setRootTreeSorting(rootTreeSorting.val());
                    loader_.sortRootTree(rootTreeSorting.val());
                    loader_.rebuildMapTreeLayout();
                    loader_.refreshMap(options);
                    loader_.editionMode(options);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed refresh tree map with selected sub tree sorting',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });
        subTreesSorting.puidropdown({
            change: function() {
                try {
                    options.setSubTreesSorting(subTreesSorting.val());
                    loader_.sortSubTrees(options.getSubTreesSorting());
                    loader_.rebuildMapTreeLayout();
                    loader_.refreshMap(options);
                    loader_.editionMode(options);
                } catch (e) {
                    helper_.addMsgToGrowl(e);
                    helper_.growlMsgs(
                        {
                            severity: 'error',
                            summary: 'Failed refresh tree map with selected sub tree sorting',
                            detail: 'Check the console log to know more...',
                            sticky: true
                        }
                    );
                    console.log(e.stack);
                }
            }
        });

        try {
            if (helper_.getNotifyInfo())
                notifyI.puicheckbox('check');
            if (helper_.getNotifyWarn())
                notifyW.puicheckbox('check');
            if (helper_.getNotifyErrs())
                notifyE.puicheckbox('check');

            options.setLayout(layout.val());
            options.edition = edition[0].checked;
            options.setURI(homeURI + "/js/taitale.samples/json/sample.taitale.input."+test.val()+".json");
            if (options.getLayout()===dic.mapLayout.MDW) {
                document.getElementById('middlewareOptions').style.display = "";
                document.getElementById('treeOptions').style.display    = "none";
            } else if (options.getLayout()===dic.mapLayout.BBTREE) {
                document.getElementById('middlewareOptions').style.display = "none";
                document.getElementById('treeOptions').style.display    = "";
            } else {
                document.getElementById('middlewareOptions').style.display = "none";
                document.getElementById('treeOptions').style.display    = "none";
            }
            loader_.loadMap(options);
            loader_.editionMode(options);
        } catch (e) {
            helper_.addMsgToGrowl(e);
            helper_.growlMsgs(
                {
                    severity: 'error',
                    summary: 'Failed to load map',
                    detail: 'Name: '+test.val()+'<br>Layout: '+options.getLayout(),
                    sticky: true
                });
            console.log(e.stack);
        }
    });